function ajaxRequest(e,t){$.ajax({url:"http://api.openweathermap.org/data/2.5/weather?q="+e+"&units=metric&APPID=ee43646aad159fa17c9141272573d285",type:"GET",dataType:"json",success:function(e){myData={Ville:e.name,Longitude:e.coord.lon,Latitude:e.coord.lat,"Vitesse du vent":e.wind.speed,"Humidité":e.main.humidity,Pression:e.main.pressure,"Température min":e.main.temp_min,"Température max":e.main.temp_max},t(myData)},error:function(e){console.log(e)},complete:function(){console.log("complete")}})}function addElement(e){$(".content").html("");for(var t in e)$(".content").append('<div class="description">'+t+" : "+e[t]+"</div>")}console.log("link script js ok"),$("input").change(function(){ajaxRequest($(this).val(),function(e){addElement(e)})});