function ajaxRequest(e,t){$.ajax({url:"http://api.openweathermap.org/data/2.5/weather?q="+e+"&units=metric&APPID=ee43646aad159fa17c9141272573d285",type:"GET",dataType:"json",success:function(e){myData={Ville:e.name,Longitude:e.coord.lon,Latitude:e.coord.lat,"Vitesse du vent":e.wind.speed,"Humidité":e.main.humidity,Pression:e.main.pressure,"Température min":e.main.temp_min,"Température max":e.main.temp_max},t(myData)},error:function(e){console.log(e)},complete:function(){console.log("complete")}})}function switchElement(e){var t=e[e.length-1],i=e[e.length-2];$("#fiche2").html("");for(var a in t)$("#fiche2").append('<div class="description">'+a+" : "+t[a]+"</div>");$("#fiche1").html("");for(var a in i)$("#fiche1").append('<div class="description">'+a+" : "+i[a]+"</div>")}console.log("link script js ok");var cityArray=[];$("input").change(function(){ajaxRequest($(this).val(),function(e){cityArray.push(e),switchElement(cityArray)})});